           RWStructuredBuffer<SBinaryIntOp> g_buf : register(u0);
           [numthreads(8,8,1)]
           void main(uint GI : SV_GroupIndex) {
               SBinaryIntOp l = g_buf[GI];
               l.output1 = l.input1 >> l.input2;
               g_buf[GI] = l;
           };

