struct SBinaryFPOp {
               float input1;
               float input2;
               float output1;
               float output2;
           };
           RWStructuredBuffer<SBinaryFPOp> g_buf : register(u0);
           [numthreads(8,8,1)]
           void main(uint GI : SV_GroupIndex) {
               SBinaryFPOp l = g_buf[GI];
               l.output1 = l.input1 / l.input2;
               g_buf[GI] = l;
           };

struct SBinaryFPOp {
               float input1;
               float input2;
               float output1;
               float output2;
           };
           RWStructuredBuffer<SBinaryFPOp> g_buf : register(u0);
